mapper.select:
SELECT * FROM (SELECT p.product_no, c.content, c.title, p.product_name, p.status, p.description, rownum rm FROM product p JOIN CATEGORIES c ON c.mid_category = p.mid_category where 1 = 1 and PRODUCT_DELETE = 0 AND p.product_name LIKE '%' || #{product_name} || '%'
AND c.top_category = #{category}
ORDER BY p.product_no) WHERE 1 = 1 and rm between #{start} and #{end}

mapper.update:
update PRODUCT
set product_name = #{product_name}, description = #{description}, CATEGORY = #{category}, MID_CATEGORY =#{mid_category}
where product_no = #{product_no}

update product
set PRODUCT_DELETE = 1
where product_no = #{product_no}

update.foreach:
UPDATE PRODUCT
SET STATUS = #{productDto.status}
WHERE PRODUCT_NO = #{productDto.product_no}

mapper.select:
select count(*)
from product
where product_name = #{product_name}
