mapper.select:
SELECT * FROM ( SELECT ROW_NUMBER() OVER (ORDER BY s.sales_date DESC) AS rn, s.sales_date, c.client_no, MIN(s.title) AS title, MIN(c.client_name) AS client_name, MIN(e.emp_name) AS emp_name, COUNT(sd.product_no) AS count, SUM(NVL(sd.quantity, 0)) AS totalQuantity, SUM(NVL(sd.price, 0) * NVL(sd.quantity, 0)) AS totalPrice, TO_CHAR(MIN(s.req_delivery_date), 'YYYY-MM-DD') AS req_delivery_date, MIN(CASE s.status WHEN 0 THEN '대기' WHEN 1 THEN '부분출고' WHEN 2 THEN '완료' ELSE '알 수 없음' END) AS status FROM SALES s LEFT JOIN CLIENTS c ON s.client_no = c.client_no LEFT JOIN SALES_DETAILS sd ON s.sales_date = sd.sales_date AND s.client_no = sd.client_no LEFT JOIN EMP e ON s.emp_no = e.emp_no WHERE 1=1
AND s.sales_date >= TO_DATE(#{startDate}, 'YYYY-MM-DD')
AND s.sales_date <= TO_DATE(#{endDate}, 'YYYY-MM-DD')
AND LOWER(c.client_name) LIKE LOWER('%' || #{client_name} || '%')
AND s.status = #{status}
GROUP BY s.sales_date, c.client_no, s.status ) WHERE rn BETWEEN #{start} AND #{end}

mapper.update:
UPDATE SALES
SET title = #{title},
    req_delivery_date = #{req_delivery_date},
    remarks = #{remarks}
WHERE sales_date = #{sales_date} 
AND client_no = #{client_no}

mapper.delete:
DELETE FROM SALES_DETAILS 
WHERE sales_date = #{sales_date} 
AND client_no = #{client_no}
AND status = 0

DELETE FROM SALES 
     WHERE sales_date = #{sales_date} 
     AND client_no = #{client_no} 
     AND status = 0
