mapper.select:
SELECT * FROM ( SELECT ROW_NUMBER() OVER (ORDER BY sd.sales_date DESC) AS rn, sd.sales_date, c.client_no, MIN(c.client_name) AS client_name, MIN(e.emp_name) AS emp_name, COUNT(sd.product_no) AS count, SUM(sd.quantity) AS totalQuantity, SUM(NVL(sd.price, 0) * NVL(sd.quantity, 0)) AS totalPrice, TO_CHAR(MIN(s.req_delivery_date), 'YYYY-MM-DD') AS req_Delivery_Date, MIN(CASE WHEN sd.status = 0 THEN '대기' ELSE '알 수 없음' END) AS status FROM SALES_DETAILS sd LEFT JOIN CLIENTS c ON sd.client_no = c.client_no LEFT JOIN SALES s ON sd.sales_date = s.sales_date AND sd.client_no = s.client_no LEFT JOIN EMP e ON s.emp_no = e.emp_no -- 수주담당자(영업부서) WHERE sd.status = 0
AND s.sales_date >= TO_DATE(#{startDate}, 'YYYY-MM-DD')
AND s.sales_date <= TO_DATE(#{endDate}, 'YYYY-MM-DD')
AND LOWER(c.client_name) LIKE LOWER('%' || #{client_name} || '%')
AND s.req_delivery_date = #{req_delivery_date}
GROUP BY sd.sales_date, c.client_no ) WHERE rn BETWEEN #{start} AND #{end}

mapper.update:
UPDATE SALES
SET status = #{status}
WHERE sales_date = #{sales_date} 
	AND client_no = #{client_no}

UPDATE SALES_DETAILS
SET status = #{status}
WHERE sales_date = #{sales_date} 
	AND client_no = #{client_no} 
	AND product_no = #{product_no}
